package ClassExercise;

public class TestIncrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//ilnc with i++;
		int i = 10; //declare i and assign value;
		int ilnc = i++; //declare ilnc ;
		
		System.out.println("The result of ilnc is " + ilnc + ".");
		System.out.println("The result of i is " + i + ".");
//ilnc with ++i;	
		ilnc = ++i; // declare ilnc and assign other value
		
		System.out.println("The result of ilnc is " + ilnc + ".");
		System.out.println("The result of i is " + i + ".");
	}

}
